package pow;
import java.util.Scanner;
public class pow {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Enter two valus => ");
		Scanner sc = new Scanner(System.in);
		int a =sc.nextInt();
		int b =sc.nextInt();
		System.out.println("Egual => "+Math.pow(a, b));

	}

}
